import { BrowserRouter as Router, Route } from "react-router-dom";
import Login from "./components/Login/Login";
import SelectedMailHome from "./components/Home/SelectedMailHome/SelectedMailHome";
import Navigationbar from "./components/Navigationbar/Navigationbar";
import SelectedMailHistory from "./components/History/SelectedMailHistory/SelectedMailHistory";
import History from "./components/History/History";
import Home from "./components/Home/Home";
import Compose from "./components/Compose/Compose";
import Profile from "./components/Profile/Profile"
import { useState } from "react";

function App(props) {

  const [token, setToken] = useState('')

  return (
    <div className="App">

      <Router>
        <Route 
          path='/'
          exact
          render={(props) => (
            <Login {...props} token={token} setToken={(token)=>{setToken(token)}} />
          )}
        />
        <Route 
          path='/home'
          render={(props) => (
            <Home {...props} token={token} />
          )}
        />
        <Route
          path='/history'
          render={(props)=>{
            <History {...props} token={token} />
          }}
        />
        <Route
          path='/compose'
          render={(props)=>{
            <Compose {...props} token={token} />
          }}
        />
      </Router>
    </div>
  );
}

export default App;
